<?php
	session_start();
	define( 'ABSPATH', dirname(__FILE__) . '/' );

	require(ABSPATH."includes/config.php");		$pageFile="index.php";		if(isset($_GET["searching"]) && !empty($_GET["searching"])) {		$_GET["searching"]=htmlentities(utf8_decode($_GET["searching"]));		$pageFile="search.php";	} elseif(isset($_GET["page"])) {		$pageFile="not_found.php";		switch($_GET["page"]) {			case "index":				$pageFile="index.php";			break;			case "read_story":				if(isset($_GET["id"])) {					$pageFile="read_story_index.php";				}			break;			case "register":				if(!isset($_SESSION["login"])) {					$pageFile="register-1.php";				} else {					header("Location:./");				}			break;			case "register_end":				if(isset($_SESSION["justRegistred"])) {					$pageFile="register-2.php";				} else {					header("Location:./");				}			break;		}	}
